package com.ssafy.algo;

public class Main548 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		for (int i = 2; i <= 4; i++) {
			for (int j = 1; j <= 5; j++)
				System.out.printf("%2d *%2d =%3d   ", i, j, (i * j));
			System.out.println();
		}
	}

}
